INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('Mario Kart 8 solo','Mario Kart 8 est un jeu de course. Mario et ses amis s''y affrontent a bord de karts ou de motos sur 32 circuits comprenant aussi bien des passages sous l''eau ou dans les airs que des loopings. Douze joueurs peuvent s''affronter en ligne ou 4 en local.',1,'E',1,1,'Jeu de karting de Nintendo');
