INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('World of warcraft','World of Warcraft vous transporte dans le monde heroic-fantasy d''Azeroth où la guerre fait rage entre la Horde et l''Alliance. Affronter des hordes de monstres seul ou en groupe, constituer des guildes, et tellement plus encore.',1,'T',19,4,'Jeu de collection d''équpement');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('World of warcraft : Cataclysm','La 3ème extension de World of Warcraft, Cataclysm. le grand retour du tout puissant Deathwing qui va entraîner dans son sillage un changement radical de la topographie des continents. Arriver jusqu''au niveau 85 avec 2 nouvelles races',1,'T',19,4,'Jeu de collection d''équpement');
