INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('Fallout 3','Fallout 3 est un jeu de rôle en 3D se déroulant dans un monde post-apocalyptique. Le joueur y fait évoluer son personnage comme il le souhaite et choisit la façon dont il va communiquer et interagir avec les autres personnages. ',1,'M',43,1,'Jeu de Survie Post-Apocalyptique');
