INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('The Elder Scrolls V : Skyrim','L''histoire se déroule dans la contrée de Skyrim, près de 200 ans après les événements narrés dans le précédent opus. L''assassinat du Haut-Roi de Bordeciel a plongé le pays dans la guerre civile et pour couronner le tout, c''est le moment que choisit Alduin pour réapparaître.',1,'M',19,1,'Jeu de découverte dans un univers médiéval');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('The Elder Scrolls IV : Oblivion','Jeu de rôle se passant dans un grand monde ouvert, The Elder Scrolls IV : Oblivion permet au joueur d''incarner un héros guerrier. Il peut explorer la carte gigantesque pour découvrir des donjons, vaincre des boss, découvrir des trésors fabuleux, et accomplir des quêtes.',1,'M',19,1,'Jeu de découverte dans un univers médiéval');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('Destiny','Destiny est un MMO FPS dans lequel le joueur se bat dans un contexte futuriste. Celui-ci doit lutter contre des hordes d''aliens. Celui-ci peut améliorer ses armes en récoltant des matériaux dans les zones du jeu, puis participer à des combats où la stratégie prime.',1,'T',6,4,'Jeu  d''extermination d''alien');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('Destiny : Le roi des corrompus','3e Extension de Destiny avec une nouvelle planète, de nouveau ennemis, de nouvelle capacité, d''ajout au PVP et bien plus',1,'T',6,4,'Jeu  d''extermination d''alien');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('Assassins Creed Syndicate solo','Prenant place dans le Londres de l''époque victorienne, Assassin''s Creed Syndicate est un jeu d''action-aventure en monde ouvert. Parmi les nouveautés, on peut notamment citer la possibilité de se déplacer sur des éléments en mouvement (calèche par exemple).',1,'M',15,1,'Jeu d''assassinat');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('Assassins Creed Syndicate Multi en ligne','Prenant place dans le Londres de l''époque victorienne, Assassin''s Creed Syndicate est un jeu d''action-aventure en monde ouvert. Parmi les nouveautés, on peut notamment citer la possibilité de se déplacer sur des éléments en mouvement (calèche par exemple).',1,'M',15,3,'Jeu d''assassinat');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('World of tank','Jeu de stratégie et de combat, World of Tanks vous place dans un tank. Aux commandes de votre char, vous affrontez des joueurs dans des parties en 15 contre 15 quelque peu mouvementées. Plus de 60 véhicules sont disponibles. Vous pouvez améliorer votre tank grâce à des crédits remportés au fur et à mesure des parties, et également via la boutique.',1,'T',5,3,'Jeu de combat de tank');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('Counter-Strike : Global Offensive solo','Les joueurs y choisissent le camp des terroristes ou des anti-terroristes avant de s''affronter dans des batailles sans merci. Plusieurs modes de jeux sont disponibles (dont un d''entraînement) et les combattants ont accès à plus de trente armes différentes.',1,'M',6,1,'Jeu  de tir compétitif');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('Counter-Strike : Global Offensive Multi en ligne','Les joueurs y choisissent le camp des terroristes ou des anti-terroristes avant de s''affronter dans des batailles sans merci. Plusieurs modes de jeux sont disponibles (dont un d''entraînement) et les combattants ont accès à plus de trente armes différentes.',1,'M',6,3,'Jeu  de tir compétitif');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('TowerFall Ascension Solo','Jeu de combat d''archerie sur plates-formes,TowerFall Ascension est jouable jusqu''à quatre en local. Les joueurs se mesurent les uns aux autres en récupérant des objets magiques dans des coffres et en essayant d''esquiver les tirs de leurs adversaires.',1,'E 10+',2,1,'Jeu de combat à l''arc');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('TowerFall Ascension Multi local','Jeu de combat d''archerie sur plates-formes,TowerFall Ascension est jouable jusqu''à quatre en local. Les joueurs se mesurent les uns aux autres en récupérant des objets magiques dans des coffres et en essayant d''esquiver les tirs de leurs adversaires.',1,'E 10+',2,2,'Jeu de combat à l''arc');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('Skyforge','Skyforge est un MMORPG sur PC. Dans un monde de haute technologie où la fantasy se mêle à la science-fiction et les mystères antiques, Skyforge transporte les joueurs dans un univers hors du commun où les mortels et les immortels se battent pour survivre contre les dieux envahisseurs et leurs armées.',1,'RP',19,4,'Jeu de dieu');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('Team Fortress 2 Solo','Team Fortress 2 est un FPS en ligne se déroulant dans un univers cartoon et caricatural. Le joueur peut choisir entre plusieurs personnages ayant chacun des capacités différentes, trouver de nombreuses armes et affronter ses pairs en multijoueur dans de nombreuses arènes et sur 6 cartes.',1,'M',6,1,'Jeu de tir compétitif');
