INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('Dream Pinball 3D ','Dream Pinball 3D est un jeu de flipper sur PC qui propose plus de 7 tables différentes, aux thèmes divers et variés et aux nombreux combos et multiplicateurs de points. Trouvez les rampes, les souterrains et les bonus du soft pour devenir docteur ès flipper.',1,'E 10+',42,1,'Jeu de Pinball');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('Super Mario Maker','Mario Maker est un jeu de plates-formes et d''édition de niveaux pour WiiU. Le joueur peut y choisir entre une esthétique moderne ou ancienne façon NES, et créer simplement ses environnements, placer des ennemis etc.',1,'E',43,1,'Jeu de plateforme');
INSERT INTO Jeux.tblJeu(NomJeu,InfoSupJeu,Actif,CoteESRB,IdGenre,IdMode,DescJeu)VALUES('Forza Motorsport 5 solo','Forza Motorsport 5 est un jeu de course orienté simulation sur Xbox One. Le joueur peut y parcourir 14 circuits allant de Yas Marina à Spa Francorchamps au volant de 200 voitures différentes. L''IA s''adapte sur le comportement de vrais joueurs tandis qu''un mode multijoueur en ligne ou en écran splitté complète le tableau.',1,'E',1,1,'Jeu de course automobile');
