INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('AnAct','Analyse de l''activité','Saisir la réalité et la complexité des activités humaines dans une situation précise','Analyse de la demande, Entretiens, Observations ouverte, Observation systématique');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('AuCon','Auto confrontation','Comprendre comment le joueur contruit mentalement son activité et quels facteurs influencent cette construction','Filmer le joueur pendant l''activité et le laisser commenter son comportement en visionnant le film');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('RePhy','Recueil des mesures physiologiques','Indiquer l''état de vigilance ou émotionnel d''une personne face à une certaine situation','Recueillir la fréquence cardiaque, l''activité électrodermal et d''autres mesures d''un joueur en réponse à une certaine situation');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('AnHeu','Analyse heuristique','Test le respect des règles de l''art et les critères ergonomiques d''un interface','Inspecter une interface à partir d''une grille d''heuristiques afin de détecter les aspects positifs et négatifs, réaliser par plusieur ergonomes');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('CoWal','Cognitive walkthrough','tester la logique interactive du système, évaluer les interfaces d''un jeu','Phase de préparation, de d''évaluation et d''analyse');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('EnTre','Entretien','Déterminer les avis, les jugements, les opinions, les croyances, les émotions par rapport à un sujet précis','Prendre un RDV avec un joueur ciblé, interviewer le joueur sur le jeu pour avoir ces avis sur un jeu, analyser ces réaction et réponse','test très long à faire à grande échelle');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('FoGro','Focus group','Regrouper divers genres de joueur différent, poser des question préalablement déterminer, un script. Enregistré la discution et noter les point qui ressort');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('AnLog','Analyse des logs','Placer des log à divers moment pour relevé les évènements déclancher par un joueur durant qu''il joue au jeu et analyser les résultat');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('PlPap','Playtests sur prototypes papier','Construire un prototype papier du jeu et le tester avec plusieur joueur et un modérateur qui s''assure que les joueurs sont pas perdu. Analyser les retours des joueur et leurs action en jeu');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('PlSce','Playtests scénarisés sur prototypes interactif','Tester des séquences particulière d''un jeu, observé la difficulter, la prise en main, le gameplay et l''amusement du testeur');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('PlOuv','Playtests ouverts sur prototypes interactifs','Tester le jeu, laisser le joueur jouer sans contrainte et analyser ces réactions.');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('QuEst','Questionnaires','Créer un questionnaire avec des questions précise, proposer au joueur de le compléter et analyser les retours. Ce test peu être complémentaire à un autre');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('PrVer','Protocoles verbaux','Demander à un testeur de penser à haute voix lors de la réalisation des tâches que l''expérimentateur lui demande d''effectuer');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('OcUlo','Oculométrie','Suivre les mouvements oculaires d''un joueur en jeu');
INSERT INTO Test.tblTypeTest('CodeTypeTest','NomTypeTest','ObjectifTypeTest','DescTypeTest','CommentaireTest') values('DiDoc','Digital script doctor','Passer en revue les différents actes et séquences constitutifs de la progression du joueur dans la trame narrative');
